    private void jButton_esittirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_esittirActionPerformed
       
        double sonuc=0;
        
        
   if(islem.compareTo("+")==0) {
 sonuc = Double.parseDouble(hafiza) + Integer.parseInt(jTextField_gosterge.getText());
    // jTextField_gosterge.setText(String.valueOf(sonuc));
         
        }
   
   else if (islem.compareTo("-")==0){
        sonuc = Double.parseDouble(hafiza) - Integer.parseInt(jTextField_gosterge.getText());
     // jTextField_gosterge.setText(String.valueOf(sonuc));
             
   }
   else if (islem.compareTo("*")==0) {
       
        sonuc = Double.parseDouble(hafiza) * Integer.parseInt(jTextField_gosterge.getText());
 //    jTextField_gosterge.setText(String.valueOf(sonuc));
              
   }
    else if (islem.compareTo("/")==0 ) {
       
        sonuc = Double.parseDouble(hafiza) / Integer.parseInt(jTextField_gosterge.getText());
        
    // jTextField_gosterge.setText(String.valueOf(sonuc));
    
    
             
   }
   String s = String.valueOf(sonuc);
   
//   if((s.substring(s.length()-2, s.length())).compareTo(".0")==0) {
//     s = s.substring(0,s.length()-2);
//     
     
   // }
   
  if( s.endsWith(".0")) {
        
        s = s.substring(0, s.length() -2);
        
    }
   
    jTextField_gosterge.setText(s);
    }//GEN-LAST:event_jButton_esittirActionPerformed

    
    
    
