    private void jButton_karekokActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_karekokActionPerformed
        // TODO add your handling code here:
        double sonuc = Double.parseDouble(jTextField_gosterge.getText());
        
        sonuc = Math.sqrt(sonuc);
        String s = String.valueOf(sonuc);
        if( s.endsWith(".0")) {
        
        s = s.substring(0, s.length() -2);
        
    }
        jTextField_gosterge.setText(s);
        
        
        
    }//GEN-LAST:event_jButton_karekokActionPerformed

    
    
    
    private void jButton_artieksiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_artieksiActionPerformed
        // TODO add your handling code here:
        String s = jTextField_gosterge.getText();
        
        
      if(jTextField_gosterge.getText().startsWith("-")) {
          
          s = s.substring(1, s.length());
          
          
      }else {
          s = "-" +s;
          
      }
       jTextField_gosterge.setText(s);
        
        
    }//GEN-LAST:event_jButton_artieksiActionPerformed

